mapper.select:
SELECT t.shop, t.line, t. ZONE, t.equipment, ISNULL(t.facility_desc,N'无描述') AS 'facility_desc', t.station, t.facility_id, t.job_id, t.start_time, t.end_time, t.duration, t.fault_word1, t.fault_word2, t.fault_word3, t.fault_description, t.reason_code, t.reason_description, ISNULL(t.pos_word31, 0) AS 'pos_word31', (SELECT sum(duration) as 'total_duration' FROM PMC_BIW_FAULT WHERE 1 = 1
) as total_duration FROM PMC.dbo.PMC_BIW_FAULT t where 1 = 1
ORDER BY t.facility_id asc,t.start_time DESC

    
	select sum(t.duration) duration from PMC.dbo.PMC_BIW_FAULT t
    WHERE 1 = 1
    
			AND t.shop =#{shop}
		
		
			AND t.line =#{area}
		
		
			AND t. zone =#{zone}
		
		
		   AND t.station=#{station}
		
		
			AND t.equipment = #{equipment}
		
		
			AND t.job_id = #{jobId}
		
		
			AND t.shift =#{shift}
		
		
			AND start_Time >= CONVERT(datetime2,#{startTime},121)
		
		
			AND end_Time <= CONVERT(datetime2,#{endTime},121)
		
		 and fault_word3 !=0 
		 and duration is not NULL

with CTE as ( select COUNT(1) "occ" , SUM(duration) "loss", facility_id, DATEPART(WK,start_time) "weekNo2", Min(start_time)"min_start_time" from PMC_BIW_FAULT where 1 = 1
AND shop = #{shop}
AND line = #{area}
AND ZONE = #{zone}
AND shift = #{shift}
and fault_word3 !=0 and end_time is not null and start_time between DATEADD(wk,-12,DATEADD(wk, DATEDIFF(wk,0,getdate()), 0)) and getdate() group by facility_id,DATEPART(WK,start_time) ) select RN = dense_rank() over (partition by facility_id order by facility_id, "min_start_time" desc),* from CTE

select top 10 facility_id "facilityId2", SUM(duration)/60 "duration" from PMC_BIW_FAULT where 1 = 1 and start_time between DATEADD(wk,-12,DATEADD(wk, DATEDIFF(wk,0,getdate()), 0)) and DATEADD(wk,0,DATEADD(wk, DATEDIFF(wk,0,getdate()), 0))
AND shop = #{shop}
AND line = #{area}
AND ZONE = #{zone}
AND shift = #{shift}
and fault_word3 !=0 and end_time is not null group by facility_id order by SUM(duration) desc

	SELECT TOP 10
	facility_id1,
	fault_description1,
	Occurrences1,
	Duration1
   FROM
	(
	  Select 
	  ROW_NUMBER() OVER (PARTITION BY fault_description ORDER BY TotalDuration desc) ROWNUMS,
	  facility_id "facility_id1",
	  fault_description "fault_description1",
	  Occurrences "Occurrences1",
	  TotalDuration "Duration1"
	  FROM
	  (
	  select facility_id,fault_description,count(fault_description) Occurrences,sum(convert(float,datediff(SS,start_time,Current_End_Time))/60) TotalDuration
	  from(
	  select fault_description,facility_id,start_time,ISNULL(end_time,GETDATE()) Current_End_Time
	  from PMC_BIW_FAULT with (nolock)
	  where 1 = 1
		 
			AND start_time between #{startTime} and #{endTime}
		
		
			AND	shop = #{shop}
		
		
			AND line = #{area}
		
		
			AND ZONE = #{zone}
		
	  and end_time is not null
	  and tav_flag!=1
	  )T
	  group by facility_id,fault_description
	  )ALL_DATA
	  )FINAL
  where ROWNUMS = 1
  
  	order by Duration1 desc;
  
  
  	order by Occurrences1 desc;

	SELECT TOP 10
	facility_id1,
	fault_description1,
	Occurrences1,
	Duration1
   FROM
	(
	  Select 
	  ROW_NUMBER() OVER (PARTITION BY fault_description ORDER BY TotalDuration desc) ROWNUMS,
	  facility_id "facility_id1",
	  fault_description "fault_description1",
	  Occurrences "Occurrences1",
	  TotalDuration "Duration1"
	  FROM
	  (
	  select facility_id,fault_description,count(fault_description) Occurrences,sum(convert(float,datediff(SS,start_time,Current_End_Time))/60) TotalDuration
	  from(
	  select fault_description,facility_id,start_time,ISNULL(end_time,GETDATE()) Current_End_Time
	  from PMC_BIW_FAULT with (nolock)
	  where 1 = 1
		 
			AND start_time between #{startTime} and #{endTime}
		
		
			AND	shop = #{shop}
		
		
			AND line = #{area}
		
		
			AND ZONE = #{zone}
		
	  and end_time is not null
	  and tav_flag!=1
	  )T
	  group by facility_id,fault_description
	  )ALL_DATA
	  )FINAL
  where ROWNUMS = 1
  
  	order by Duration1 desc;
  
  
  	order by Occurrences1 desc;
