mapper.select:
SELECT TOP 10 facility_id1, fault_description1, Occurrences1, Duration1 FROM ( Select ROW_NUMBER() OVER (PARTITION BY fault_description ORDER BY TotalDuration desc) ROWNUMS, facility_id "facility_id1", fault_description "fault_description1", Occurrences "Occurrences1", TotalDuration "Duration1" FROM ( select facility_id, fault_description, count(fault_description) Occurrences, sum(convert(float,datediff(SS,start_time,Current_End_Time))/60) TotalDuration from( select fault_description, facility_id,start_time, ISNULL(end_time,GETDATE()) Current_End_Time from BIW3.dbo.Final_BIW3_FAULT t where 1 = 1
AND CONVERT (VARCHAR(10), start_time, 105) IN
#{_date}
and area!='BDC' and tav_flag!=1 )T group by facility_id,fault_description )ALL_DATA )FINAL where ROWNUMS = 1 order by Duration1 desc, Occurrences1 desc, facility_id1,fault_description1;
