mapper.select:
select avg(c.target_mtbf_f) As Target_MTBF ,avg(c.target_tav_f) as Target_TAV from at_pmc_masterdata_config c where c.shop_s = 'CS01_BS3'
and c.line_s = #{area}
and c.zone_s = #{zone}
and c.production_flag_y = 1
and c.eol_flag_y = 1

(SELECT WeekRange , DATEADD(wk,WeekRange,DATEADD(wk, DATEDIFF(wk,0,getdate()), 0)) "MondayOfCurrentWeek", DATEPART("wk", DATEADD(wk,WeekRange,DATEADD(wk, DATEDIFF(wk,0,getdate()), 0))) "WeekNumber" FROM ( SELECT 0 AS WeekRange UNION ALL SELECT -1 AS WeekRange UNION ALL SELECT -2 AS WeekRange UNION ALL SELECT -3 AS WeekRange UNION ALL SELECT -4 AS WeekRange UNION ALL SELECT -5 AS WeekRange UNION ALL SELECT -6 AS WeekRange UNION ALL SELECT -7 AS WeekRange UNION ALL SELECT -8 AS WeekRange UNION ALL SELECT -9 AS WeekRange UNION ALL SELECT -10 AS WeekRange UNION ALL SELECT -11 AS WeekRange UNION ALL SELECT -12 AS WeekRange UNION ALL SELECT -13 AS WeekRange UNION ALL SELECT -14 AS WeekRange UNION ALL SELECT -15 AS WeekRange UNION ALL SELECT -16 AS WeekRange UNION ALL SELECT -17 AS WeekRange )t2 )Q left join ( select TOP 18 DATEADD(dd, -(DATEPART(DW, start_time)-2), start_time) "Week", tech_avail*100 "TAV",tar_tech_avail "Target_TAV", mtbf/60 "MTBF",tar_mtbf/60 "Target_MTBF",start_time from PMC.dbo.PMC_9PANEL_TA t where 1 = 1
AND shop = #{shop}
AND line = #{area}
AND ZONE = #{zone}
AND shift = #{shift}
AND levels = #{level}
and frequency=2 order by t.start_time desc )P on DATEPART(WK,Week)=WeekNumber;

select 
	t.zone ,
	avg(t.tar_tech_avail) tar_tech_avail ,
	avg(t.tech_avail) tech_avail,
	avg(t.tech_avail*100-t.tar_tech_avail) "Gap",
	(DATEADD(wk,DATEDIFF(wk,0,t.start_time),0))"firstDay"
from PMC_9PANEL_TA t LEFT JOIN Final_BIW3_OPR o 
ON t.zone = o.zone
where 1=1

	and  t.start_time between #{startTime} and #{endTime}


	AND t.shop = #{shop}


	AND t.line = #{area}


	AND t.ZONE = #{zone}


	AND t.shift = #{shift}


	AND t.jobId = #{jobId}

and o.production_volume >= 0
and o.frequency='Shiftwise'
group by t.zone,(DATEADD(wk,DATEDIFF(wk,0,t.start_time),0))
order by t.zone,(DATEADD(wk,DATEDIFF(wk,0,t.start_time),0))

select count(good_cycle_count)"prod_vol" ,zone
from PMC_BIW_CYCLE
where 1 = 1

	and end_time between #{startTime} and #{endTime}


	AND area = #{area}


	AND ZONE = #{zone}


	AND shift = #{shift}


	AND job_id = #{jobId}

and production_flag = 1
group by zone

with CTE as( select ROW_NUMBER () OVER (order by P."Week" desc) RN,isNull(P.[TAV%],0) "TAV %",Q.MondayOfCurrentWeek"Week",isNULL(P.tar_TA,0) "Tar_TA" from (SELECT WeekRange , DATEADD(wk,WeekRange,DATEADD(wk, DATEDIFF(wk,0,getdate()), 0)) "MondayOfCurrentWeek", DATEPART("wk", DATEADD(wk,WeekRange,DATEADD(wk, DATEDIFF(wk,0,getdate()), 0))) "WeekNumber" FROM ( SELECT 0 AS WeekRange UNION ALL SELECT -1 AS WeekRange UNION ALL SELECT -2 AS WeekRange UNION ALL SELECT -3 AS WeekRange UNION ALL SELECT -4 AS WeekRange UNION ALL SELECT -5 AS WeekRange UNION ALL SELECT -6 AS WeekRange UNION ALL SELECT -7 AS WeekRange UNION ALL SELECT -8 AS WeekRange UNION ALL SELECT -9 AS WeekRange UNION ALL SELECT -10 AS WeekRange UNION ALL SELECT -11 AS WeekRange UNION ALL SELECT -12 AS WeekRange UNION ALL SELECT -13 AS WeekRange UNION ALL SELECT -14 AS WeekRange UNION ALL SELECT -15 AS WeekRange UNION ALL SELECT -16 AS WeekRange UNION ALL SELECT -17 AS WeekRange UNION ALL SELECT -18 AS WeekRange UNION ALL SELECT -19 AS WeekRange UNION ALL SELECT -20 AS WeekRange UNION ALL SELECT -21 AS WeekRange UNION ALL SELECT -22 AS WeekRange UNION ALL SELECT -23 AS WeekRange UNION ALL SELECT -24 AS WeekRange ) t2)Q left join (select TOP 25 DATEADD(dd, -(DATEPART(DW, start_time)-2), start_time) "Week",tech_avail*100 "TAV%",tar_tech_avail "tar_TA" from PMC_9PANEL_TA where 1 = 1
AND shop = #{shop}
AND ZONE = #{zone}
AND shift = #{shift}
and levels='zone' and frequency=2 order by start_time desc)P on DATEPART(ISOWK,Week)=WeekNumber)
select Top 24 [Current Row].[TAV %]-[Next Row].[TAV %]"TAV",[Current Row].Week "Week",[Current Row].Tar_TA "Target_TAV" FROM CTE [Current Row] LEFT JOIN CTE [Next Row] ON [Next Row].RN = [Current Row].RN + 1

select
equipment,workingDay,tar_tech_avail,tech_avail,good_part_count,downtime/60"downtime",fault_occ,build_time/60"build_time",facility_id
from PMC_9PANEL_TA
where 1 = 1

	AND shop =#{shop}


	AND line =#{area}


	AND Zone =#{zone}


	AND shift =#{shift}


	AND start_time >= CONVERT(datetime2,#{startTime},121)


	AND start_time <= CONVERT(datetime2,#{endTime},121)

and levels = 'zone'
and frequency = 1
order by facility_id
